import { defineConfig } from 'vite'
import react from '@vitejs/plugin-react'
import { VitePWA } from 'vite-plugin-pwa'
import fs from 'fs'
import path from 'path'

// Read version from public/version.json (generated by generate-version.js)
let appVersion = ''
try {
    const versionFile = path.resolve(__dirname, 'public/version.json')
    if (fs.existsSync(versionFile)) {
        const data = JSON.parse(fs.readFileSync(versionFile, 'utf-8'))
        appVersion = data.version
    }
} catch (e) {
    console.warn('Failed to read version.json')
}

// Fallback to direct git hash if version is still empty
if (!appVersion) {
    try {
        const { execSync } = await import('child_process')
        appVersion = execSync('git rev-parse --short HEAD').toString().trim()
        console.log(`[ViteConfig] Version fallback to git: ${appVersion}`)
    } catch (e) {
        appVersion = Date.now().toString()
        console.warn(`[ViteConfig] Version fallback to timestamp: ${appVersion}`)
    }
}

// https://vitejs.dev/config/
export default defineConfig({
    plugins: [
        react(),
        VitePWA({
            // ✅ 간소화: 앱 설치 기능만 유지, 캐싱은 비활성화
            registerType: 'autoUpdate',
            injectRegister: 'auto',
            workbox: {
                skipWaiting: true,
                clientsClaim: true,
                cleanupOutdatedCaches: true,
                // ❌ JS/CSS/HTML 캐싱 완전 비활성화 - 브라우저 기본 캐싱만 사용
                globPatterns: [], // 아무것도 precache 안 함
                navigateFallback: null,
                // ✅ 런타임 캐싱도 최소화: 폰트만 캐시
                runtimeCaching: [
                    // Fonts만 캐시 (거의 안 바뀜)
                    {
                        urlPattern: /^https:\/\/fonts\.googleapis\.com\/.*/i,
                        handler: 'CacheFirst',
                        options: {
                            cacheName: 'google-fonts-cache',
                            expiration: { maxEntries: 10, maxAgeSeconds: 60 * 60 * 24 * 365 }
                        }
                    },
                    {
                        urlPattern: /^https:\/\/fonts\.gstatic\.com\/.*/i,
                        handler: 'CacheFirst',
                        options: {
                            cacheName: 'gstatic-fonts-cache',
                            expiration: { maxEntries: 20, maxAgeSeconds: 60 * 60 * 24 * 365 }
                        }
                    }
                    // 나머지는 모두 브라우저 기본 동작 (네트워크 요청)
                ]
            },
            includeAssets: ['favicon.ico', 'apple-touch-icon.png', 'mask-icon.svg'],
            manifest: {
                name: 'Grapplay',
                short_name: 'Grapplay',
                description: '프리미엄 주짓수 기술 영상 플랫폼',
                theme_color: '#09090b',
                background_color: '#09090b',
                display: 'standalone',
                start_url: '/',
                icons: [
                    {
                        src: 'pwa-192x192.png',
                        sizes: '192x192',
                        type: 'image/png',
                        purpose: 'any maskable'
                    },
                    {
                        src: 'pwa-512x512.png',
                        sizes: '512x512',
                        type: 'image/png',
                        purpose: 'any maskable'
                    }
                ]
            }
        }),
    ],
    define: {
        'import.meta.env.VITE_APP_VERSION': JSON.stringify(appVersion),
    },
    build: {
        rollupOptions: {
            output: {
                manualChunks: (id) => {
                    // React 사용하지 않는 순수 JS 라이브러리만 분리 (안전)
                    if (id.includes('@supabase')) return 'supabase';
                    if (id.includes('@sentry') && !id.includes('@sentry/react')) return 'sentry';
                    if (id.includes('date-fns')) return 'date-fns';
                    if (id.includes('@vimeo/player')) return 'vimeo';
                    if (id.includes('tus-js-client')) return 'tus';

                    // React 관련 모든 것은 기본 청크로 (분리하지 않음)
                    // react, react-dom, react-query, framer-motion, reactflow 등
                }
            }
        },
        chunkSizeWarningLimit: 1000, // 경고 임계값 상향 (1MB)
        minify: 'terser', // 더 강력한 압축
        terserOptions: {
            compress: {
                drop_console: true, // 프로덕션에서 console.log 제거
                drop_debugger: true,
                pure_funcs: ['console.log', 'console.info', 'console.debug']
            }
        },
        sourcemap: false, // 프로덕션 빌드에서 소스맵 제거 (크기 절약)
    },
    esbuild: {
        drop: process.env.NODE_ENV === 'production' ? ['console', 'debugger'] : [],
    },
    server: {
        // port: 8080, // Removed to allow default (5173) or flexible port
        open: true,
        proxy: {
            '/process': {
                target: 'http://localhost:8081',
                changeOrigin: true,
            },
            '/api': {
                target: 'http://localhost:8081',
                changeOrigin: true,
            },
            '/temp': {
                target: 'http://localhost:8081',
                changeOrigin: true,
            }
        }
    }
})

